INSERT INTO wp_postmeta VALUES("8043","487","_section_3_heading","field_551aa32f97734");
INSERT INTO wp_postmeta VALUES("8044","487","section_3_subhead","Our customer portal gives you access to your calibration information from your facility or other remote locations.");
INSERT INTO wp_postmeta VALUES("8045","487","_section_3_subhead","field_551aa33997735");
INSERT INTO wp_postmeta VALUES("8046","487","section_3_body","Our web-based customer portal allows you to utilize our asset management services to their fullest.   We balance multi-layer security and read-only capability of core calibration data with  user-by-user defined access to features like customer shipper creation and current asset status.  It is cross- platform and cross- browser compatible, with support for a wide variety of mobile devices. You access your data wherever and whenever you need it most.");
INSERT INTO wp_postmeta VALUES("8047","487","_section_3_body","field_551aa35197736");
