INSERT INTO wp_postmeta VALUES("10585","18","_page_title","field_5526aece3aaef");
INSERT INTO wp_postmeta VALUES("10586","18","s1_sub_heading","NovaStar Solutions customers rest easy knowing their used I.T. assets are being handled in the proper manner. We are R2 (Responsible Recycling) and ISO 14001:2004 certified as well as a Microsoft Registered Refurbisher.\r\n\r\nTo obtain these certifications, NovaStar developed an Environmental Management System (EMS) proving our remarketing services have minimal effect on the environment and comply with related laws, regulations and requirements. And being part of the Microsoft Registered Refurbisher program enhances our product and service offerings, thus helping to maximize values we get for partners we remarket on behalf of. Our metrology calibration laboratory also maintains ISO 17025:2005 certifications and A2LA accreditation.");
INSERT INTO wp_postmeta VALUES("10587","18","_s1_sub_heading","field_5526aea93aaee");
