INSERT INTO wp_postmeta VALUES("11398","588","_first_section_heading","field_551c212e4b337");
INSERT INTO wp_postmeta VALUES("11399","588","first_section_content","<h3>Acquisition</h3>\r\n<ul>\r\n	<li>Assess Client’s IT needs</li>\r\n	<li>Configure new or refurbished hardware</li>\r\n</ul>\r\n<h3>Deployment</h3>\r\n<ul>\r\n	<li>Shipping, storing, unpacking, asset tagging</li>\r\n	<li>Load Software Image &amp; test</li>\r\n	<li>Deliver</li>\r\n	<li>Installs &amp; cascades</li>\r\n	<li>Remove packaging</li>\r\n	<li>Certified Repair &amp; Maintenance</li>\r\n</ul>\r\n<h3>Retirement</h3>\r\n<ul>\r\n	<li>De-install equipment</li>\r\n	<li>Lease termination at pick-up</li>\r\n	<li>Audit, test, disk wipe or destruction</li>\r\n	<li>Refurbish &amp; appraise</li>\r\n	<li>Remarket to recover asset value</li>\r\n	<li>Certified e-waste disposal</li>\r\n</ul>\r\n<h3>Asset Management</h3>\r\n<ul>\r\n	<li>IT asset database</li>\r\n	<li>Tracking &amp; reporting</li>\r\n</ul>");
