INSERT INTO wp_postmeta VALUES("11411","588","block_2_content","Offering a complete IT lifecycle solution, Novastar manages everything from new asset acquisition, logistics, imaging, installation, maintenance, repair, to end-of-life (or lease) options for your IT assets, recovering value whenever possible and providing a true Cradle-to-grave solution.");
