INSERT INTO wp_postmeta VALUES("16017","824","s2_body","Dassault Systeme’s Simulia CAE  solution puts the power to evaluate performance, reliability and safety of materials and products in computer simulation before the time and expense required for physical prototypes:\r\n<ul>\r\n	<li>Multiphysics Simulation</li>\r\n	<li>Modeling &amp; Visualization</li>\r\n	<li>Design Simulation</li>\r\n	<li>Design Optimization</li>\r\n	<li>Simulation Process Management</li>\r\n</ul>");
INSERT INTO wp_postmeta VALUES("16018","824","_s2_body","field_5535251182840");
INSERT INTO wp_postmeta VALUES("16019","824","s2_callout","Part of the Dassault’s 3DEXPERIENCE Platform, the Simulia Unified Abaqus FEA suite is utilized by multiple industries and disciplines to achieve their design simulation requirements:\r\n<ul>\r\n	<li><strong>Automotive Industry</strong> – up front design validation to minimize costs &amp; maximize efficiencies</li>\r\n	<li><strong>Aerospace &amp; Defense</strong> – evaluate alternative designs collaboratively and efficiently</li>\r\n	<li><strong>Architecture, Engineering and Construction</strong> – structural analysis with sophisticated solvers</li>\r\n	<li><strong>Consumer Goods –</strong> save time and money in the pre-prototype stage</li>\r\n	<li><strong>Energy &amp; Utilities</strong> – meet environmental and safety standards for stress and wear</li>\r\n	<li><strong>Hi Technology</strong> - analysis of thermal, electrical, mechanical and moisture-sensitivity loads</li>\r\n	<li><strong>Industrial Equipment</strong> - predict complex behavior of parts, large scale structures &amp; systems</li>\r\n	<li><strong>Life Sciences</strong> – Simulate loads applied to both medical devices and human tissue</li>\r\n</ul>");
