INSERT INTO wp_postmeta VALUES("17129","870","s1_body","CATIA Computer aided design CAD software solutions allow users to create 3D assemblies for a wide variety of engineering processes including:\r\n<ul>\r\n	<li>Cast and forged parts</li>\r\n	<li>Plastic injection and molding</li>\r\n	<li>Composites part design and manufacturing</li>\r\n	<li>Sheet metal parts design</li>\r\n	<li>Advanced fastening operations</li>\r\n</ul>\r\n<strong>CATIA 3D Modeling tools enable defining a complete mechanical product with improved productivity.</strong>\r\n<h4>Benefits</h4>\r\n<ul>\r\n	<li>Create complete 3D parts from sketch to full assemblies</li>\r\n	<li>Easy transition from 2D to 3D design</li>\r\n	<li>Consistent drawing updates reduces user tasks</li>\r\n	<li>Manufacturing process intent captured early in design stages</li>\r\n	<li>Multiple tooling design applications</li>\r\n	<li>Specification  driven modeling approach to mechanical surfacing</li>\r\n</ul>");
INSERT INTO wp_postmeta VALUES("17130","870","_s1_body","field_553524c0051df");
INSERT INTO wp_postmeta VALUES("17131","870","s1_callout","<h4>Applications:</h4>\r\n<ul>\r\n	<li><strong>Mechanical Engineering </strong>– high productivity and change flexibility</li>\r\n	<li><strong>Model Based Definition </strong>– single source geometric product definition</li>\r\n	<li><strong>Composites Engineering </strong>– design analysis and manufacture on one platform</li>\r\n	<li><strong>Sheet Metal Design</strong> – quality sheet metal part design fast</li>\r\n	<li><strong>Fastener Design</strong> – easily design complex fasteners with scalability</li>\r\n	<li><strong>Plastic Part and Mold Design</strong> – high quality, quickly, while controlling costs</li>\r\n	<li><strong>Electrical Systems</strong> – wire harnesses, cable systems &amp; electrical components</li>\r\n	<li><strong>Fluid Systems</strong> – specification driven design environment</li>\r\n	<li><strong>Architecture Engineering &amp; Construction</strong> – plan and model buildings</li>\r\n	<li><strong>Machine &amp; Equipment Design</strong> – industrial equipment engineering</li>\r\n	<li><strong>Transport and Mobility Design</strong> – integrate chassis and powertrains</li>\r\n	<li><strong>Marine &amp; Offshore</strong> - ship and space design applications</li>\r\n	<li><strong>High Tech engineering</strong> – sheet metal, plastics parts, circuit boards and more</li>\r\n	<li><strong>Knowledge Engineering</strong> – apply knowledge rules throughout the product design</li>\r\n</ul>");
